import { useEffect, useRef, useState } from "react";

export default function SpeechGuide({ text, lang = "hi-IN", rate = 1, pitch = 1, volume = 1 }) {
  const utteranceRef = useRef(null);
  const [available, setAvailable] = useState(false);
  const [speaking, setSpeaking] = useState(false);

  useEffect(() => {
    const synth = window.speechSynthesis;
    if (!synth) return;

    const update = () => setAvailable(synth.getVoices().length > 0);
    update();
    if (typeof window !== "undefined") {
      window.speechSynthesis.onvoiceschanged = update;
    }
    return () => {
      if (typeof window !== "undefined") {
        window.speechSynthesis.onvoiceschanged = null;
      }
    };
  }, []);

  const getVoice = () => {
    const voices = window.speechSynthesis?.getVoices?.() || [];
    return (
      voices.find(v => v.lang?.toLowerCase() === lang.toLowerCase()) ||
      voices.find(v => v.lang?.toLowerCase().startsWith("hi")) ||
      voices.find(v => v.lang?.toLowerCase().startsWith("en")) ||
      null
    );
  };

  const speak = () => {
    if (!window.speechSynthesis) return;
    if (!text) return;
    stop();
    const u = new SpeechSynthesisUtterance(text);
    const voice = getVoice();
    if (voice) u.voice = voice;
    u.lang = voice?.lang || lang;
    u.rate = rate;
    u.pitch = pitch;
    u.volume = volume;
    u.onend = () => setSpeaking(false);
    u.onerror = () => setSpeaking(false);
    utteranceRef.current = u;
    setSpeaking(true);
    window.speechSynthesis.speak(u);
  };

  const stop = () => {
    if (!window.speechSynthesis) return;
    window.speechSynthesis.cancel();
    setSpeaking(false);
  };

  return (
    <div className="inline-flex gap-2">
      <button
        type="button"
        onClick={speaking ? stop : speak}
        disabled={!available}
        className="px-4 py-2 rounded-md text-white bg-[#2563EB] hover:bg-[#1d4ed8] disabled:opacity-50"
      >
        {speaking ? "Stop" : "Listen in Hindi"}
      </button>
    </div>
  );
}
